			rootDiv.append('<span style="cursor: pointer;" id="' + this.rootId + '_name" style="float: left;">' + this.name + '</span>&nbsp;');

			$("#" + this.rootId + " span").click(function(){
				Modalinfo.info(that.defValue, "Pattern statement");
			})
